package org.opentele.server.citizen

import grails.test.spock.IntegrationSpec
import grails.validation.ValidationException
import org.opentele.server.model.Patient
import org.opentele.server.model.RealTimeCtg

class RealTimeCTGServiceIntegrationSpec extends IntegrationSpec {

    def realTimeCTGService
    def patient

    static String XML = ""
    static String soapAction = "http://tempuri.org/IOpenTeleRT/NewMessage"


    def setup() {
        patient = Patient.findById(1)
        RealTimeCtg.findAll().each {
            it.delete()
        }
    }

    def 'can create realtimeCTG from params and save it'() {
        setup:
        def params = [:]
        params.xml = XML
        params.soapAction = soapAction
        params.patient = patient
	
        when:
        realTimeCTGService.save(params)

        then:
        RealTimeCtg.findAll().size() == 1
        def ctg = RealTimeCtg.findByXml(XML)

        ctg.xml == XML
        ctg.soapAction == soapAction
        ctg.patient == patient
    }

    def 'can delete all real time CTGs for patient'() {
        setup:
        def ctg1 = RealTimeCtg.build(patient: patient)
        def ctg2 = RealTimeCtg.build(patient: patient)
        def ctg3 = RealTimeCtg.build(patient: Patient.findById(2))
        ctg1.save(failOnError: true)
        ctg2.save(failOnError: true)
        ctg3.save(failOnError: true)

        RealTimeCtg.countByPatient(patient) > 0

        when:
        realTimeCTGService.deleteFor(patient)

        then:
        RealTimeCtg.countByPatient(patient) == 0
        RealTimeCtg.countByPatient(Patient.findById(2)) == 1
    }

    def 'will fail on validation errors'() {
        setup:
        def params = [:]
        params.xml = XML
        params.patient = patient

        when:
        realTimeCTGService.save(params)

        then:

        ValidationException ex = thrown()
    }
}
